const item = $input.item.json;
const companyDomain = item['Company Domain'];

// Check if this company domain was contacted recently
const lastContactDate = item['Last Contact Date'];

// If company domain already has a contact in progress, skip
if (lastContactDate && lastContactDate !== '') {
  const daysSince = (new Date() - new Date(lastContactDate)) / (1000 * 60 * 60 * 24);

  // Skip if contacted within last 14 days (gives time for sequence to complete)
  if (daysSince < 14) {
    console.log(`⏭️ Skipping ${item['Business Name']} - company contacted ${Math.round(daysSince)} days ago`);
    return null; // Skip this lead
  }
}

// Company clear to contact
return { json: item };

